import React, { Component } from "react";

class Cart extends Component {
  state = {
    arr: []
  };

  deleteFromCart=(id)=>{
      fetch(`/shopping-cart/${id}`, {
        method: "DELETE"
      })
        .then(response => response.json())
        .then(json => console.log(json));
  }

  updateQuantity=(id)=>{
      fetch(`/shopping-cart/${id.substring(5)}`, {
        method: "PATCH",
        body: JSON.stringify({ quantity: x.value }),
        headers: { "Content-Type": "application/json" }
      })
        .then(response => response.json())
        .then(json => console.log(json));
  }

  componentDidMount() {
    fetch("/shopping-cart")
      .then(res => res.json())
      .then(arr => this.setState({ arr }));
  }

  render() {
    return (
      <div>
        {this.state.arr.map(x => (
          <tr key={x._id}>
            <th scope="row" className="border-0">
              <div className="p-2">
                <img
                  src={x.img}
                  width="100"
                  className="img-fluid rounded shadow"
                />
                <div className="ml-3 d-inline-block align-middle">
                  <h5 className="mb-0">
                    {" "}
                    <span className="text-dark d-inline-block align-middle">
                      ${x.brand} ${x.model}
                    </span>
                  </h5>

                  <span className="text-muted font-weight-normal font-italic d-block">
                    <div className="5">
                      <div className="1">
                        Lorem ipsum dolor sit amet consectetur, adipisicing
                        elit.{" "}
                      </div>
                    </div>
                  </span>
                </div>
              </div>
            </th>

            <td className="border-0 align-middle price"> ${x.price} $</td>

            <td className="align-middle">
              <input
                className="w-50 quant"
                onChange={this.updateQuantity(x._id)}
                type="number"
                min="1"
                value={x.quantity}
              />
            </td>

            <td className="align-middle">
              <button
                className="del"

                onClick={this.deleteFromCart(x._id)}
              >
                delete
              </button>
            </td>
          </tr>
        ))}
      </div>
    );
  }
}

export default Cart;
