import React, { Component, Fragment } from 'react';
import {BrowserRouter as Router, Switch, Link, NavLink} from 'react-router-dom';

class ProductsList extends Component {
    state = {
      arr: []
    }

    componentDidMount() {
       fetch(`/product-listing/`)
        .then(res=>res.json())
        .then(arr=>this.setState({arr}))
    }

    render() {
        return (
          <Fragment>

            <header>
              <strong>HandiPhone eShop</strong>{" "}
              <Link  to="/cart">
                <i className="fas fa-shopping-cart fa-2x"></i>
              </Link>
            </header>
            {this.state.arr.map(x => (
              <div className="card text-white bg-dark">
                <img src={x.img} className="card-img-top" alt="phonePic" />
                <div className="card-body">
                  <h5 className="card-title">
                    {x.brand} {x.model}
                  </h5>
                  <p className="card-text">Color: {x.color}</p>
                  <p className="card-text">Price: {x.price} $</p>
                  {/* <Link
                    to="/product?"+"{x._id}"
                    className="btn btn-primary"
                  >
                    View Phone
                  </Link> */}
                </div>
              </div>
            ))}
          </Fragment>
        );
    }
}

export default ProductsList
