import React, { Component, Fragment } from 'react';
import {BrowserRouter as Router, Route, Switch, Link, NavLink} from 'react-router-dom';

const id = window.location.search.substring(1);

export class Product extends Component {
    state = {
       arr: []
    }

    componentDidMount(){
        fetch("/product-view/" + { id })
          .then(res => res.json())
          .then(arr => this.setState({ arr }));
    }

    render() {
        return (
            <Router>
              <Fragment>
                <div style={{display: "flex", padding: "40px 100px", width: "100%"}}>
        <div className="col-lg-4 mx col-md-6 mb-4" >
          <div className="card h-100 w-75 mx-auto">
            <img className="card-img-top" src={product.img} alt=""/>>
            <div className="card-body">
              <h4 className="card-title text-center ">
                {product.brand} {product.model}
              </h4>
              <h6 class="font-weight-bolder">Price: {product.price}</h6>
            </div>
          </div>
        </div>
        <div style={{padding: "30px", width: "500px"}}> Description: <br/>
           {product.description}
        </div>
        </div>
        </Fragment>
            </Router>
        )
    }
}

export default Product
