import React, { Component, Fragment } from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Link,
  NavLink
} from "react-router-dom";
import Product from "./Product";

class ProductsList extends Component {
  state = {
    arr: []
  };

  componentDidMount() {
    fetch(`/product-listing/`)
      .then(res => res.json())
      .then(arr => this.setState({ arr }));
  }

  render() {
    return (
      <Fragment>
        <Router>

                <Switch>
                  <Route path="/" exact render={()=>{return (
                                                       <Fragment>
                                                         <header>
                                                           <strong>
                                                             HandiPhone eShop
                                                           </strong>{" "}
                                                           <Link to="/cart">
                                                             <i className="fas fa-shopping-cart fa-2x"></i>
                                                           </Link>
                                                         </header>
                                                         {this.state.arr.map(
                                                           x => (
                                                             <div
                                                               className="card text-white bg-dark"
                                                               key={x._id}
                                                             >
                                                               <img
                                                                 src={x.img}
                                                                 className="card-img-top"
                                                                 alt="phonePic"
                                                               />
                                                               <div className="card-body">
                                                                 <h5 className="card-title">
                                                                   {x.brand}{" "}
                                                                   {x.model}
                                                                 </h5>
                                                                 <p className="card-text">
                                                                   Color:{" "}
                                                                   {x.color}
                                                                 </p>
                                                                 <p className="card-text">
                                                                   Price:{" "}
                                                                   {x.price} $
                                                                 </p>
                                                                 <Link
                                                                   to="/product?5e1bacc10b38c11e6fc605c0"
                                                                   className="btn btn-primary"
                                                                 >
                                                                   View Phone
                                                                 </Link>{" "}
                                                               </div>
                                                               <Route
                                                                 path={
                                                                   "/product?" +
                                                                   x._id
                                                                 }
                                                                 exact
                                                                 component={
                                                                   Product
                                                                 }
                                                               />
                                                             </div>
                                                           )
                                                         )}
                                                       </Fragment>
                                                     );}}/>

                </Switch>

        </Router>
      </Fragment>
    );
  }
}

export default ProductsList;
